import { useState, useEffect } from "react";
import classes from "./App.module.scss";
import { ReactComponent as DayCloudyIcon } from "./images/day-cloudy.svg";
import { ReactComponent as AirFlowIcon } from "./images/airFlow.svg";
import { ReactComponent as RainIcon } from "./images/rain.svg";
import { ReactComponent as RefreshIcon } from "./images/refresh.svg";
import { ReactComponent as LoadingIcon } from "./images/loading.svg";
import dayjs from "dayjs";

const AUTH_KEY = "CWB-D99CD594-E7C2-445E-B41D-BE075D34F43A";
const LOCATION = "臺北";
const LOCATION_FORECAST = "臺北市";

function App() {
  const [weatherElement, setWeatherElement] = useState({
    locationName: "臺北市",
    description: "多雲時晴",
    windSpeed: 1.1,
    temperature: 22.9,
    rainPossibility: 48.3,
    observationTime: "2020-12-12 22:10:00",
    comfortability: "舒適至悶熱",
    weatherCode: 0,
    isLoading: true,
  });

  const {
    observationTime,
    locationName,
    description,
    windSpeed,
    temperature,
    rainPossibility,
    isLoading,
  } = weatherElement;

  const fetchCurrentWeather = () => {
    setWeatherElement((prevState) => ({ ...prevState, isLoading: true }));
    fetch(
      `https://opendata.cwb.gov.tw/api/v1/rest/datastore/O-A0003-001?Authorization=${AUTH_KEY}&locationName=${LOCATION}`
    )
      .then((res) => res.json())
      .then((data) => {
        const locationData = data.records.location[0];
        const weatherElements = locationData.weatherElement.reduce(
          (neededElements, item) => {
            if (["WDSD", "TEMP"].includes(item.elementName)) {
              neededElements[item.elementName] = item.elementValue;
            }
            return neededElements;
          },
          {}
        );
        setWeatherElement((prevState) => ({
          ...prevState,
          locationName: locationData.locationName,

          windSpeed: weatherElements.WDSD,
          temperature: weatherElements.TEMP,

          observationTime: locationData.time.obsTime,
          isLoading: false,
        }));
      });
  };

  const fetchWeatherForecast = () => {
    fetch(
      `https://opendata.cwb.gov.tw/api/v1/rest/datastore/F-C0032-001?Authorization=${AUTH_KEY}&locationName=${LOCATION_FORECAST}`
    )
      .then((res) => res.json())
      .then((data) => {
        const locationData = data.records.location[0];
        const weatherElements = locationData.weatherElement.reduce(
          (neededElements, item) => {
            if (["Wx", "PoP", "CI"].includes(item.elementName)) {
              neededElements[item.elementName] = item.time[0].parameter;
            }
            return neededElements;
          },
          {}
        );
        setWeatherElement({
          description: weatherElements.Wx.parameterName,
          weatherCode: weatherElements.Wx.parameterValue,
          rainPossibility: weatherElements.PoP.parameterName,
          comfortability: weatherElements.CI.parameterName,
        });
      });
  };

  useEffect(() => {
    fetchCurrentWeather();
    fetchWeatherForecast();
  }, []);

  return (
    <div className={classes.container}>
      <div className={classes.weathercard}>
        <div className={classes.location}>{locationName}</div>
        <div className={classes.description}>{description}</div>
        <div className={classes.currentweather}>
          <div className={classes.temperture}>
            {Math.round(temperature)}
            <div className={classes.celsius}>°C</div>
          </div>
          <DayCloudyIcon></DayCloudyIcon>
        </div>
        <div className={classes.airFlow}>
          <AirFlowIcon></AirFlowIcon>
          {windSpeed}m/h
        </div>
        <div className={classes.rain}>
          <RainIcon></RainIcon>
          {rainPossibility}%
        </div>
        <div
          className={isLoading ? classes.refreshloading : classes.refresh}
          onClick={fetchCurrentWeather}
        >
          最後觀測時間:
          {new Intl.DateTimeFormat("zh-TW", {
            hour: "numeric",
            minute: "numeric",
          }).format(dayjs(observationTime))}{" "}
          {isLoading ? (
            <LoadingIcon></LoadingIcon>
          ) : (
            <RefreshIcon></RefreshIcon>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
