import classes from "./App.module.scss";
import { ReactComponent as DayCloudyIcon } from "./images/day-cloudy.svg";
import { ReactComponent as AirFlowIcon } from "./images/airFlow.svg";
import { ReactComponent as RainIcon } from "./images/rain.svg";
import { ReactComponent as RefreshIcon } from "./images/refresh.svg";
import styled from "@emotion/styled";

function App() {
  const DayCloudy = styled(DayCloudyIcon)`
    flex-basis: 30%;
  `;

  const theme = {
    leight: {
      backgroundColor: "#ededed",
      foregroundColor: "#f9f9f9",
      boxShadow: "0 1px 3px 0 #999999",
      titleColor: "#212121",
      temperatureColor: "#757575",
      textColor: "#828282",
    },
    dark: {
      backgroundColor: "#1F2022",
      foregroundColor: "#121416",
      boxShadow:
        "0 1px 4px 0 rgba(12, 12, 13, 0.2), 0 0 0 1px rgba(0, 0, 0, 0.15)",
      titleColor: "#f9f9fa",
      temperatureColor: "#dddddd",
      textColor: "#cccccc",
    },
  };

  return (
    <div className={classes.container} style={{}}>
      <div className={classes.weathercard}>
        <div className={classes.location}>台北市</div>
        <div className={classes.description}>多雲時晴</div>
        <div className={classes.currentweather}>
          <div className={classes.temperture}>
            23
            <div className={classes.celsius}>°C</div>
          </div>
          <DayCloudyIcon></DayCloudyIcon>
        </div>
        <div className={classes.airFlow}>
          <AirFlowIcon></AirFlowIcon>23m/h
        </div>
        <div className={classes.rain}>
          <RainIcon></RainIcon>48%
        </div>
        <div className={classes.refresh}>
          {" "}
          最後觀測時間:上午 12:03<RefreshIcon></RefreshIcon>
        </div>
      </div>
    </div>
  );
}

export default App;
